import './Chat.css';
import Contact from '../Contact/Contact.js';
import Conversation from '../Conversation/Conversation.js';
import { useState } from 'react';

function Chat() {

    const [convPeople, setConvPeople] = useState([])

    const RecupConvPeople = (convpeople) => {
        alert('coucou')
        setConvPeople(convpeople)
        document.getElementsByClassName('laConversation').style.display = 'block';
    }

    const conversation = [
        {
            "user_surname": "Moreau",
            "user_firstname": "Juliette",
            "user_picture": "",
            "user_is_online": false,
            "chat": [
                {
                    "id": 1,
                    "content": "Bonjour",
                    "send_time": "2022-02-10T00:00:00",
                    "status": "Lu",
                    "who": true
                },
                {
                    "id": 2,
                    "content": "Bonjour ça va ?",
                    "send_time": "2022-02-11T00:00:00",
                    "status": "Envoyer",
                    "who": false
                },
                {
                    "id": 3,
                    "content": "Oui et vous ?",
                    "send_time": "2022-02-12T00:00:00",
                    "status": "Envoyer",
                    "who": true
                },
                {
                    "id": 4,
                    "content": "Bonjour, après avoir vu ton CV ainsi que ton profile, nous souhaiterons avoir un entretien avec vous dans nos locaux !",
                    "send_time": "2022-02-13T00:00:00",
                    "status": "Envoyer",
                    "who": false
                }
            ]
        },
        {
            "user_surname": "Rousseau",
            "user_firstname": "Paul",
            "user_picture": "",
            "user_is_online": false,
            "chat": [
                {
                    "id": 5,
                    "content": "Bonjour",
                    "send_time": "2022-02-14T00:00:00",
                    "status": "Lu",
                    "who": true
                },
                {
                    "id": 6,
                    "content": "Bonjour ça va ?",
                    "send_time": "2022-02-15T00:00:00",
                    "status": "Lu",
                    "who": false
                },
                {
                    "id": 7,
                    "content": "Oui et vous ?",
                    "send_time": "2022-02-16T00:00:00",
                    "status": "Lu",
                    "who": true
                },
                {
                    "id": 8,
                    "content": "Bonjour, après avoir vu ton CV ainsi que ton profile, nous souhaiterons avoir un entretien avec vous dans nos locaux !",
                    "send_time": "2022-02-17T00:00:00",
                    "status": "Lu",
                    "who": false
                }
            ]
        }
    ]

    const Affiche_Conversation = (conv) => {
        RecupConvPeople(conv)
    }

    return (
        <>
            <div className="Chat">
                <div className="Cherche_Contact">
                    <input type="text" /><i class="fa fa-search" aria-hidden="true"></i>
                </div>
                <div className="Liste_Contact">
                    {
                        conversation.map((index) => (
                            <div onClick={() => Affiche_Conversation(index)} className="LeContact">
                                <Contact convs={index} />
                            </div>
                        ))
                    }
                </div>
            </div>
            <div className="laConversation">
                <Conversation detail={convPeople}/>
            </div>
            

        </>
    );

}

export default Chat;
