import React, { useContext, useReducer } from 'react'
import { myAppContextPopUp } from '../../Stores/PopUpContext'
import StudentPopUp from '../AnnoucementPopUp/StudentAnnoucementPopUp/StudentAnnoucementPopUp'
import RecruiterPopUp from '../AnnoucementPopUp/RecruiterAnnoucementPopUp/RecruiterAnnoucementPopUp'

function PopUp() {

    const popUpContext = useContext(myAppContextPopUp)
    const [popUpShow, dispatchPopUpShow] = useReducer(popUpShowReducer, <></>)

    const popUpShowReducer = () => {
        console.log(popUpContext)
        if (popUpContext.popUp.home) {
            switch (popUpContext.popUp.home.type) {
                case 'student':
                    return <StudentPopUp></StudentPopUp>
                case 'recruiter':
                    return <RecruiterPopUp></RecruiterPopUp>
                default:
                    return <></>
            }
        }
    }

    return (
        <div className='pop_up'>
            {popUpShow}
        </div>
    )
}

export default PopUp
