import React, { useContext } from 'react'
import '../Annoucement.css'
import { myAppContextPopUp } from '../../../Stores/PopUpContext'

function RecuiterAnnoucement(props) {

  const popUp = useContext(myAppContextPopUp)

  const click = () => {
    console.log(props)
    popUp.dispatchPopUp({ type: 'HOME', payload: { type: 'recruiter', value: props.annoucement } })
  }

  return (
    // <div className='annoucement' onClick={click}>
    //   <h4>{props.annoucement.title}</h4>
    //   <h5>{props.annoucement.localisation}</h5>
    //   <p>{props.annoucement.description}</p>
    // </div >
    <div className="lesAnnonces">
      <div className="Entreprise">
        {/* <img src={image} /> */}
        <h3>{props.announcement.title}</h3>
        <i aria-hidden="true" className={props.announcement.favorite ? 'fa fa-heart fa-2x actif' : 'fa fa-heart fa-2x notactif'}></i>
      </div>
      <p className="Title">{props.announcement.title}</p>
      <div className="Info">
        <p><i className="fa fa-map-marker" aria-hidden="true"></i>{props.announcement.user.company.city}</p>
      </div>
      <button>Voir l'annonce</button>
    </div>
  )
}

export default RecuiterAnnoucement
