import React, { useContext } from 'react'

import { myAppContextUserInfo } from '../../../Stores/UserInfoContext'

function ChatList() {

    const userInfo = useContext(myAppContextUserInfo)

    return (
        <div className='ChatList'>
            {
                userInfo.userInfo.convs.map((chat, index) => {
                    return (
                        <div key={index} className='ChatList__chat'>
                            <div className='ChatList__chat__img'>
                                <img src={chat.user_picture} alt={chat.surname} />
                            </div>
                            <div className='ChatList__chat__info'>
                                <div className='ChatList__chat__info__name'>{chat.surname}</div>
                                <div className='ChatList__chat__info__lastMsg'>{chat.chat.pop()}</div>
                            </div>
                        </div>
                    )
                })
            }
        </div>
    )
}

export default ChatList
