import './Chat.css';
import Contact from '../Contact/Contact.js';

function Chat() {

    return (
        <>
            <div className="Chat">
                <div className="Cherche_Contact">
                    <input type="text"/><i class="fa fa-search" aria-hidden="true"></i>
                </div>
                <div className="Liste_Contact">
                    {
                        /* 
                            les convs recupérer de l'api seront comme sa:
                            un bloc par contact avec les donnée du gars a qui on parle et les messages
                            {
                                "user_surname",
                                "user_firstname",
                                "user_picture",
                                "user_is_online", // a voir avec le serveur
                                "chat" : [
                                    {
                                        "id",
                                        "content",
                                        "send_time",
                                        "status"
                                    },
                                    {
                                        "id",
                                        "content",
                                        "send_time",
                                        "status"
                                    },
                                    {
                                        ...
                                    }
                                ]
                            },
                            {
                                ...
                            }

                            Tu pourras alors faire un map sur les différents bloc de contact
                            Dans les props: 
                                - Etat correpond à si il y a un message non lu
                                - Nombre correspond au nombre de message nom lu
                        */
                    }
                    <Contact convs={{Nombre: 2}}/>
                    <Contact convs={{Nombre: 3}}/>
                    <Contact convs={{Nombre: 1}}/>
                    <Contact convs={{Nombre: 2}}/>
                    <Contact convs={{Nombre: 0}}/>
                    <Contact convs={{Nombre: 0}}/>
                    <Contact convs={{Nombre: 0}}/>
                    <Contact convs={{Nombre: 0}}/>
                    <Contact convs={{Nombre: 0}}/>
                    <Contact convs={{Nombre: 0}}/>
                    <Contact convs={{Nombre: 0}}/>
                    <Contact convs={{Nombre: 0}}/>
                    <Contact convs={{Nombre: 0}}/>
                    <Contact convs={{Nombre: 0}}/>
                </div>
            </div>
        </>
    );

}

export default Chat;
