import './PetiteAnnonce.css';
import image from '../../Pictures/sncf.png';
import { useReducer } from 'react';

function PetiteAnnonce() {

    const myReducerFavorite = (state, action) => {
        switch(action.type)
        {
            case 'menu':
                if(action.payload == 1)
                {
                    return {connect: true, collection: false, recherche: false}
                }
                if(action.payload == 2)
                {
                    return {connect: false, collection: true, recherche: false} 
                }
                if(action.payload == 3)
                {
                    return {connect: false, collection: false, recherche: true} 
                }
            

            default:
                return state
        }
    }

    const [favorite, setFavorite] = useReducer(myReducerFavorite, {favorite: true})

    function getRandomBoolean() {
        alert(Math.random() < 0.5);
        return Math.random() < 0.5;
    }

    return (
        <>
            <div className="lesAnnonces">
                <div className="Entreprise">
                    <img src={image}/>
                    <h3>Nom entreprise</h3>
                    <i class="fa fa-heart fa-2x" aria-hidden="true" className={getRandomBoolean() ? 'actif' : 'notactif'}></i>
                </div>
                <h2>Intitul√© de l'annonce</h2>
                <div className="Info">
                    <p><i class="fa fa-map-marker" aria-hidden="true"></i>Localisation</p>
                    <p><i class="fa fa-file-text-o" aria-hidden="true"></i>Type de contrat</p>
                </div>
                <button>Voir l'annonce</button>
            </div>
        </>
    );

}

export default PetiteAnnonce;
