import React, { useState } from 'react'
import ChatList from '../../Components/Chat/ChatList/ChatList'

function Chat() {

  const [selectedChat, setSelectedChat] = useState(null)

  const contentChat = () => {
    if (selectedChat) {
      return <div className='ChatContent'>
        <img src={selectedChat.user_picture} />
        <h1>{selectedChat.user_firstname}</h1>
        <h1>{selectedChat.user_surname}</h1>
        {selectedChat.chat.map((element, index) => {
          return <p key={index}>{element}</p>
        })}
      </div>
    } else {
      return <div className='ChatContent'></div>
    }
  }
  return (
    <div className='Chat'>
      <ChatList setSelectedChat={setSelectedChat} />
      {contentChat()}
    </div>
  )
}

export default Chat
