import React, { useState } from 'react'
import './Search.css'

import Pictures from '../../Pictures/Pictures'
import SearchBar from '../../Components/SearchBar/SearchBar'
import axios from 'axios'

function Search() {

  const [isActive, setIsActive] = useState(false)
  const [cityList, setCityList] = useState([])

  const filterList = {
    diplome: [
      { id: 1, name: "Sans Diplôme" },
      { id: 2, name: "Bac" },
      { id: 3, name: "Bac + 2" },
      { id: 4, name: "Bac + 3" },

    ]
  }

  const active = () => {
    setIsActive(!isActive)
  }

  const cityIsUpdate = (event) => {
    axios.get('https://geo.api.gouv.fr/communes?nom=' + event.target.value + '&limit=5')
      .then((response) => {
        let list = []
        response.data.forEach(element => {
          list.push(element.nom)
        })
        setCityList(list)
      })
      .catch((error) => {
        console.log(error.message)
      })
  }

  return (
    <div className={isActive ? 'search' : 'search active'}>

      <div className='search_page'>
        <div className='search_bar'>
          <input type='text' placeholder='Rechercher' />
          <button><img src={Pictures.Search} alt='Search' /></button>
        </div>

      </div>

      <span></span>

      <div className='filters'>
        <button onClick={active}><img src={Pictures.Filters} alt='Filters' /></button>
        {!isActive && <div className='filter_list'>
          <div className='city'>
            <h1>Localisation</h1>
            <SearchBar change={cityIsUpdate} list={cityList} placeholder={'Rechercher une ville '} />

            <h1>Diplôme</h1>
            <select >
              {
                filterList.diplome.map((element, index) => (
                 <option key={index} value={element.id}>{element.name}</option>
                ))
              }
            </select>
          </div>
        </div>}
      </div>

    </div>
  )
}

export default Search
