import React, { createContext, useReducer } from "react";

export const myAppContextPopUp = createContext({
    popUp: {
        home: {
            homeComponent: <></>,
            homeValue: null,
        }
    },
    dispatchPopUp: (action) => { },
})

export const MyAppContextPopUpProvider = ({ children }) => {

    const popUpReducer = (state, action) => {
        switch (action.type) {
            case 'HOME':
                console.log(state.home)
                switch (popUpContext.popUp.home.type) {
                    case 'student':
                        return <StudentPopUp></StudentPopUp>
                    case 'recruiter':
                        return <RecruiterPopUp></RecruiterPopUp>
                    default:
                        return <></>
                }
                break
            default:
                return state
        }
    }

    const [popUp, dispatchPopUp] = useReducer(popUpReducer, {
        home: {
            homeComponent: <></>,
            homeValue: null,
        }
    })

    return (
        <myAppContextPopUp.Provider value={{
            popUp: popUp,
            dispatchPopUp: dispatchPopUp
        }}>
            {children}
        </myAppContextPopUp.Provider>
    )
}
