import React, { useContext } from 'react'
import { myAppContextUserInfo } from '../../../Stores/UserInfoContext';
import Loading from '../../../Components/Loading/Loading';
import useAxios from '../../../Axios/axios'

function ConnectLoader(props) {

  const userInfo = useContext(myAppContextUserInfo)
  const { cancel, data, error, loaded } = useAxios('https://localhost:7061/api/User/testconnect?id=' + "sophie.dupont@gmail.com" + '&psw=' + "test", 'get', null)

  if(loaded){
    userInfo.dispatchUserInfo({ type: 'CONNECT', payload: data })
    setTimeout(console.log(1), 5000);
    setTimeout(props.setConnect(true), 5000);
  }

  return (
    <Loading />
  )

}

export default ConnectLoader
