import React, { useState } from 'react'
import './Search.css'

import Pictures from '../../Pictures/Pictures'
import axios from 'axios'

function Search() {

  const [isActive, setIsActive] = useState(false)
  const [cityList, setCityList] = useState([])

  const active = () => {
    setIsActive(!isActive)
  }

  const cityIsUpdate = (event) => {
    axios.get('https://geo.api.gouv.fr/communes?nom=' + event.target.value + '&boost=population&limit=5')
      .then((response) => {
        setCityList([])
        response.data.forEach(element => {
          setCityList([...cityList, element.nom])
        })
      })
      .catch((error) => {
        console.log(error.message)
      })
  }

  return (
    <div className={isActive ? 'search' : 'search active'}>

      <div className='search_page'>
        <div className='search_bar'>
          <input type='text' placeholder='Rechercher' />
          <button><img src={Pictures.Search} alt='Search' /></button>
        </div>

      </div>

      <span></span>

      <div className='filters'>
        <button onClick={active}><img src={Pictures.Filters} alt='Filters' /></button>
        <div className='filter_list'>
          <div className='city'>
            <h1>Localisation</h1>
            <input list="city" type="text" id="city" onChange={cityIsUpdate} />
            <datalist id="city">
              {cityList.map((element, index) => (
                <option key={index} value={element} />
              ))}
            </datalist>
            <button onClick={() => (console.log(cityList))}></button>
          </div>
        </div>
      </div>

    </div>
  )
}

export default Search
