import './Conversation.css';
import image from '../../Pictures/user.png';

function Conversation(props) {

    if(props.detail.length != 0)
    {
        return (
            <>
                <div className="Conversation">
                    <div className="NamePeople">
                        <img src={image} />
                    </div>
                    <div className="Messages"></div>
                    <div className="Ecrire">
                        <textarea placeholder='Type your message here'></textarea><i onClick={() => Affiche_Conversation(index)} class="fa fa-paper-plane fa-2x" aria-hidden="true"></i>
                    </div>
                </div>
            </>
        );
    }
    else
    {
        return (
            <></>
        );
    }

    

}

export default Conversation;
