import React, { useState } from 'react'

function SearchBar(props) {

    const [inputValue, setInputValue] = useState('')
    const [isShow, setIsShow] = useState(false)

    const select = (e) => {
        setInputValue(e.target.textContent)
        setIsShow(false)
    }

    const change = (e) => {
        if (e.target.value === '') {
            setIsShow(false)
        }
        else {
            setIsShow(true)
        }
        setInputValue(e.target.value)
        props.change(e)
    }

    const enter = (e) => {
        if(e.key === 'Enter'){
            const value = {target:{value:props.list[0]}}
            console.log(value)
            // change(value)
        }
    }

    return (
        <div className='searchs_bar'>
            <input
                list="city"
                type="text"
                id="city"
                onChange={change}
                autoComplete='off'
                value={inputValue}
                onKeyDown={enter}
            />
            {isShow && <div className='results_list'>
                {
                    props.list.map((element, index) => {
                        return <div className='result' key={index} onClick={select}>{element}</div>
                    })
                }
            </div>}
        </div>
    )
}

export default SearchBar
