import React, { useState } from 'react'

function SearchBar(props) {

    const [inputValue, setInputValue] = useState('')
    const [selectValue, setSelectValue] = useState(false)

    const select = (e) => {
        setSelectValue(e.target.textContent)
        setInputValue(e.target.textContent)
    }

    const change = (e) => {
        setSelectValue(null)
        setInputValue(e.target.value)
        props.change(e)
    }

    const isShow = () => {
        if(inputValue === ''){
            return false
        }
        if(props.list === []){
            return false
        }
        if(selectValue === null){
            return false
        }
        return true
    }

    return (
        <div className='searchs_bar'>
            <input list="city" type="text" id="city" onChange={change} autoComplete='off' value={inputValue}/>
            {isShow && <div className='results_list'>
                {
                    props.list.map((element, index) => {
                        return <div className='result' key={index} onClick={select}>{element}</div>
                    })
                }
            </div>}
        </div>
    )
}

export default SearchBar
