import React, { useState } from 'react'
import ChatList from '../../Components/ChatList/ChatList'
import './Chat.css'

function Chat() {

  const [selectedChat, setSelectedChat] = useState(null)

  console.log(selectedChat)

  const contentChat = () => {
    if (selectedChat) {
      return <div className='chat_content'>
        <div className='head'>
          <img src={selectedChat.user_picture} />
          <h1>{selectedChat.user_firstname} {selectedChat.user_surname}</h1>
        </div>
        <div className='chats'>
        {selectedChat.chat.map((element, index) => {
          return <p key={index}>{element.content}</p>
        })}
        </div>
      </div>
    } else {
      return <div className='chat_content'></div>
    }
  }
  return (
    <div className='Chat'>
      <ChatList setSelectedChat={setSelectedChat} />
      {contentChat()}
    </div>
  )
}

export default Chat
