import './PetiteAnnonce.css';
import image from '../image/sncf.png';

function PetiteAnnonce() {

    return (
        <>
            <div className="lesAnnonces">
                <div className="Entreprise">
                    <img src={image}/>
                    <h3>Nom entreprise</h3>
                    <i class="fa fa-heart fa-2x" aria-hidden="true"></i>
                </div>
                <h2>Intitul√© de l'annonce</h2>
                <div className="Info">
                    <p><i class="fa fa-map-marker" aria-hidden="true"></i>Localisation</p>
                    <p><i class="fa fa-file-text-o" aria-hidden="true"></i>Type de contrat</p>
                </div>
                <button>Voir l'annonce</button>
            </div>
        </>
    );

}

export default PetiteAnnonce;
