import './Chat.css';
import Contact from '../Contact/Contact.js';
import Conversation from '../Conversation/Conversation.js';
import { useState } from 'react';

function Chat() {

    const [convPeople, setConvPeople] = useState([])

    const RecupConvPeople = (convpeople) => {
        setConvPeople(convpeople)
        // document.getElementsByClassName('laConversation').style.display = 'block';
        document.getElementsByClassName('laConversation')[0].style.display = 'block';
    }

    const conversation = [
        
    ]

    const Affiche_Conversation = (conv) => {
        RecupConvPeople(conv)
    }

    
    return (
        <>
            <div className="ChatAndConv">
                <div className="Chat">
                    <div className="Cherche_Contact">
                        <input type="text" /><i class="fa fa-search" aria-hidden="true"></i>
                    </div>
                    <div className="Liste_Contact">
                        {
                            conversation.map((index) => (
                                <div onClick={() => Affiche_Conversation(index)} className="LeContact">
                                    <Contact convs={index} />
                                </div>
                            ))
                        }
                    </div>
                </div>
                <div className="laConversation">
                    <Conversation detail={convPeople}/>
                </div>
            </div>

        </>
    );
    

    

}

export default Chat;
