import React, { useContext } from 'react'
import '../Announcement.css'
import { myAppContextPopUp } from '../../../Stores/PopUpContext'

function RecruiterAnnouncement(props) {

  const popUp = useContext(myAppContextPopUp)

  const seeAnnouncement = () => {
    console.log(props)
    popUp.dispatchPopUp({ type: 'HOME', payload: { type: 'recruiter', value: props.announcement } })
  }

  console.log(props.announcement.title)

  return (
    // <div className='announcement' onClick={click}>
    //   <h4>{props.announcement.title}</h4>
    //   <h5>{props.announcement.localisation}</h5>
    //   <p>{props.announcement.description}</p>
    // </div >
    
    <div className="announcement">
      <div className="recruiter">
        {/* <img src={image} /> */}
        <h3>{props.announcement.user.company.name}</h3>
        <i aria-hidden="true" className={props.announcement.favorite ? 'fa fa-heart fa-2x actif' : 'fa fa-heart fa-2x notactif'}></i>
      </div>
      <p className="Title">{props.announcement.title}</p>
      <div className="Info">
        <p><i className="fa fa-map-marker" aria-hidden="true"></i>{props.announcement.localisation}</p>
      </div>
      <button onClick={seeAnnouncement}>Voir l'annonce</button>
    </div>
  )
}

export default RecruiterAnnouncement
