import React, { useContext } from 'react'
import StudentAnnoucement from '../Annoucement/StudentAnnoucement/StudentAnnoucement'
import { Link } from 'react-router-dom'
import { myAppContextUserInfo } from '../../Stores/UserInfoContext'

function ListAnnoucement(props) {

    const userInfo = useContext(myAppContextUserInfo)

    const selection = () => {
        switch (userInfo.userInfo.type_user) {
            case 'Recruteur': {
                return userInfo.userInfo[props.type].map((annoucement, index) => (
                    <StudentAnnoucement key={index} annoucement={annoucement} />
                ))
            }
            case 'Etudiant': {
                return
                userInfo.userInfo[props.type].map((annoucement, index) => (
                    < StudentAnnoucement key={index} annoucement={annoucement} />
                ))
            }
            default: {
                return null
            }
        }
    }

    return (
        <div className='ListAnnoucement'>
            <h3>{props.title}</h3>
            {selection()}
            {props.isViewsMore && <Link to='/'><button>Voir plus</button></Link>}
        </div>
    )
}

export default ListAnnoucement
