import './Contact.css';
import image from '../../Pictures/Logo_Shippee.png';
import { useReducer, useEffect } from 'react';

function Contact(props) {

    const myReducerEtat = () => {
        if(props.convs.Etat == "Lu")
        {
            return {etat: true}
        }
        if(props.convs.Etat == "Envoyer")
        {
            return {etat: false}
        }
    }

    const [etat, setEtat] = useReducer(myReducerEtat, {etat: null})

    useEffect(() => {
        setEtat()
    })


    return (
        <>
        <div className={etat.etat ? 'Contact Lu' : 'Contact Envoyer'}>
            <div className="Contact_Picture">
                <img src={image}/>
            </div>
            <div className="Contact_Data">
                <div className="People_Number">
                    <h3 className="People">Florence - Isitech</h3>
                    <div className={etat.etat ? 'Number_Message None' : 'Number_Message Block'}>{props.convs.Nombre}</div>
                </div>
                <p className="Texte">Bonjour, apr√®s avoir vu ton CV ainsi que ton profile, nous souhaiterons avoir un entretien avec vous dans nos locaux !</p>
            </div>
        </div>
        </>
    );

}

export default Contact;
