import './PetiteAnnonce.css';
import image from '../../Pictures/sncf.png';

function PetiteAnnonce(props) {
    console.log(props)
    return (
        <>
            {
                /*
                    Ici pas complqué: 
                    A la place de image, tu met l'image de la personne ( props.announcement.user.picture )
                    Si c'est une annonce entreprise
                    A la place de Nom entreprise, tu met le nom de l'entreprise' ( props.announcement.user.company.name )
                    Si c'est une annonce étudiant
                    A la place de Nom entreprise, tu met le nom de l'etudiant ( props.announcement.user.firstname + props.announcement.user.surname )
                    A la place de Intitulé de l'annonce, tu met le titre ( props.announcement.title )
                    A la place de Localisation, tu met l'image de la personne ( props.announcement.user.company.city ou props.announcement.user.city )

                    Et a la place de props.announcement tu met props.favorite
                */
            }
            <div className="lesAnnonces">
                <div className="Entreprise">
                    <img src={image} />
                    <h3>{props.announcement.user.company.name}</h3>
                    <i aria-hidden="true" className={props.announcement.favorite ? 'fa fa-heart fa-2x actif' : 'fa fa-heart fa-2x notactif'}></i>
                </div>
                <h2>{props.announcement.title}</h2>
                <div className="Info">
                    <p><i class="fa fa-map-marker" aria-hidden="true"></i>{props.announcement.user.company.city}</p>
                </div>
                <button>Voir l'annonce</button>
            </div>
        </>
    );

}

export default PetiteAnnonce;
