import './Contact.css';
import image from '../../Pictures/Logo_Shippee.png';
import { useEffect, useState } from 'react';
import Conversation from '../Conversation/Conversation.js';

function Contact(props) {

    const [nbNonLu, setNbNonLu] = useState([])

    const CompterNbChatNonLu = () => {
        var nb = 0;
        for (let i = 0; i < props.convs.chat.length; i++) {
            if (props.convs.chat[i].status == "Envoyer" && props.convs.chat[i].who == false) {
                nb++
            }
        }
        setNbNonLu(nb)
    }

    useEffect(() => {
        CompterNbChatNonLu()
    })

    const Affiche_Conversation = () => {
        alert("oui")
        document.getElementsByClassName('Edit').style.display = 'block'
    }

    return (
        <>

            <div className={props.convs.chat[props.convs.chat.length - 1].status == "Lu" ? 'Contact Lu' : 'Contact Envoyer'}> 
                <div className="Contact_Picture">
                    <img src={image} />
                </div>
                <div className="Contact_Data">
                    <div className="People_Number">
                        <h3 className="People">{props.convs.user_surname} {props.convs.user_firstname}</h3>
                    </div>
                    <p className="Texte">{props.convs.chat[props.convs.chat.length - 1].content}</p>
                </div>
                <h3 className={props.convs.chat[props.convs.chat.length - 1].status == "Lu" ? 'None' : 'Block'}>{nbNonLu}</h3>
            </div>
            <div className="Edit">
                <Conversation />
            </div>
        </>
    );

}

export default Contact;
