import React, { useContext } from 'react'
import './ChatList.css'

import { myAppContextUserInfo } from '../../../Stores/UserInfoContext'

function ChatList(props) {

    const userInfo = useContext(myAppContextUserInfo)

    return (
        <div className='ChatList'>
            {
                userInfo.userInfo.convs.map((chat, index) => {
                    return <div className='chat_bubble' key={index} onClick={() => props.setSelectedChat(chat)}>
                        <img src={chat.user_picture} />
                        <h1>{chat.user_firstname}</h1>
                        <h1>{chat.user_surname}</h1>
                        <p>{chat.chat[chat.chat.length - 1].content}</p>
                    </div>
                })
            }
        </div>

    )
}

export default ChatList
