// import { myAppContextUserInfo } from '../../../Stores/UserInfoContext';
import Loading from '../../../Components/Loading/Loading';
// import useAxios from '../../../Axios/axios'
import axios from 'axios'

function ConnectLoader(props) {

  const [data, setData] = useState(null);
  const [error, setError] = useState("");
  const [loaded, setLoaded] = useState(false);

  // const userInfo = useContext(myAppContextUserInfo)
  // const [isLoaded, setLoaded] = useState(useAxios('https://localhost:7061/api/User/testconnect?id=' + "sophie.dupont@gmail.com" + '&psw=' + "test", 'get', null))
