import './Contact.css';
import image from '../../Pictures/Logo_Shippee.png';

function Contact(props) {

    return (
        <>
            {
                /* 
                    Une fois là, on recup le nombre qu'on à mis en paramètre.
                    Si il est à 0 alors ça veut dire que tout les messages de avec ce contact on été lu
                    Si c'est pas égale à 0 alors ça veut dire que le message à été envoyer mais pas lu
                */
            }
            <div className={props.convs.Nombre == 0 ? 'Contact Lu' : 'Contact Envoyer'}>
                <div className="Contact_Picture">
                    <img src={image} />
                </div>
                <div className="Contact_Data">
                    <div className="People_Number">
                        <h3 className="People">Florence - Isitech</h3>
                        <div className={props.convs.Nombre == 0 ? 'Number_Message None' : 'Number_Message Block'}>{props.convs.Nombre}</div>
                    </div>
                    <p className="Texte">Bonjour, après avoir vu ton CV ainsi que ton profile, nous souhaiterons avoir un entretien avec vous dans nos locaux !</p>
                </div>
            </div>
        </>
    );

}

export default Contact;
