import './Contact.css';
import image from '../../Pictures/Logo_Shippee.png';
import { useEffect } from 'react';

function Contact(props) {

    useEffect(() => {
        NbTomeByLivre()
        NbTomeByUserByLivre()
        ImageTome()
    })

    return (
        <>
            {
                /* 
                    Une fois là, on recup le nombre qu'on à mis en paramètre.
                    Si il est à 0 alors ça veut dire que tout les messages de avec ce contact on été lu
                    Si c'est pas égale à 0 alors ça veut dire que le message à été envoyer mais pas lu

                    Pas touche aux className, 
                    Contact c'est pour les deux pour la forme. Lu et Envoyer c'est pour la couleur du background
                    None c'est si il y a 0 message non lu on affiche pas le nombre de message non lu 
                    Block c'est pour l'afficher
                */
            }
            <div className={props.convs.chat[props.convs.chat.length - 1].status == "Lu" ? 'Contact Lu' : 'Contact Envoyer'}>
                <div className="Contact_Picture">
                    <img src={image} />
                </div>
                <div className="Contact_Data">
                    <div className="People_Number">
                        <h3 className="People">{props.convs.user_surname} {props.convs.user_firstname}</h3>
                        <div className={props.convs.chat[props.convs.chat.length - 1].status == "Lu" ? 'None' : 'Block'}>{props.convs.Nombre}</div>
                    </div>
                    <p className="Texte">{props.convs.chat[props.convs.chat.length - 1].content}</p>
                </div>
            </div>
        </>
    );

}

export default Contact;
