function Contact(props) {

    return (
        <>
        <div className={props.convs.Etat ? 'Contact Lu' : 'Contact Envoyer'}>
            <div className="Contact_Picture">
                <img src={image}/>
            </div>
            <div className="Contact_Data">
                <div className="People_Number">
                    <h3 className="People">Florence - Isitech</h3>
                    <div className={props.convs.Etat ? 'Number_Message None' : 'Number_Message Block'}>{props.convs.Nombre}</div>
                </div>
                <p className="Texte">Bonjour, apr√®s avoir vu ton CV ainsi que ton profile, nous souhaiterons avoir un entretien avec vous dans nos locaux !</p>
            </div>
        </div>
        </>
    );

}

export default Contact;
