import React, { useContext, useEffect } from 'react'
import { useNavigate } from 'react-router-dom';
import { myAppContextUserInfo } from '../../../Stores/UserInfoContext';
import Loading from '../../../Components/Loading/Loading';

function ConnectLoader(props) {

    const userInfo = useContext(myAppContextUserInfo)
    const navigate = useNavigate()

    useEffect(() => {
      userInfo.dispatchUserInfo({ type: 'CONNECT' , payload: { id : "sophie.dupont@gmail.com", psw: "test" }})
      props.setConnect(true)
    }, [userInfo, navigate, props])

  return (
    <Loading />
  )
}

export default ConnectLoader
